import assert from 'assert';
import { describe, it } from 'mocha';

import { disableRandomMock, initRandomMock } from '@proton/testing/lib/mockRandomValues';

import { getRandomSrpVerifier, getSrp } from '../lib/srp';
import '../test/setup';
import { AUTH_RESPONSE, FAKE_RANDOM, SERVER_MODULUS, SERVER_MODULUS_FAKE } from '../test/srp.data';

const mockRandomValues = (buf: Uint8Array) => new Uint8Array(FAKE_RANDOM.slice(0, buf.length));

describe('srp', () => {
    before(() => initRandomMock(mockRandomValues));
    after(() => disableRandomMock());

    it('should generate verifier', async () => {
        const result = await getRandomSrpVerifier({ Modulus: SERVER_MODULUS }, { password: '123' });
        assert.deepStrictEqual(result, {
            version: 4,
            salt: 'SzHkg+YYA/eN1A==',
            verifier:
                '',
        });
    });

    it('should reject verify if it is unable to verify identity', async () => {
        const promise = getRandomSrpVerifier({ Modulus: SERVER_MODULUS_FAKE }, { password: 'hello' });
        await assert.rejects(promise, {
            name: 'Error',
            message: 'Unable to verify server identity',
        });
    });

    it('should generate auth parameters', async () => {
        const result = await getSrp(AUTH_RESPONSE, { password: '123' });
        assert.deepStrictEqual(result, {
            clientEphemeral:
                '',
            clientProof:
                '',
            expectedServerProof:
                '',
            // eslint-disable-next-line max-len
            sharedSession: new Uint8Array([
                232, 138, 48, 101, 47, 10, 241, 220, 72, 46, 138, 53, 109, 224, 110, 199, 82, 78, 121, 186, 176, 217,
                74, 235, 109, 115, 239, 55, 231, 173, 71, 100, 48, 42, 27, 154, 125, 254, 69, 244, 17, 161, 184, 143,
                191, 248, 135, 102, 4, 144, 200, 240, 170, 189, 139, 106, 15, 106, 80, 55, 195, 98, 77, 203, 0, 123, 24,
                88, 130, 151, 83, 26, 68, 25, 11, 243, 179, 9, 183, 247, 210, 38, 153, 156, 163, 201, 86, 94, 227, 103,
                114, 102, 173, 36, 178, 34, 1, 191, 98, 29, 230, 191, 32, 132, 72, 182, 7, 40, 14, 103, 80, 232, 98, 43,
                78, 147, 81, 69, 162, 157, 94, 119, 7, 132, 11, 96, 241, 14, 175, 64, 43, 144, 21, 103, 246, 112, 226,
                239, 94, 25, 107, 47, 208, 98, 58, 67, 49, 242, 68, 156, 46, 16, 103, 89, 33, 73, 211, 139, 190, 198,
                157, 57, 226, 186, 159, 227, 71, 70, 208, 230, 164, 179, 250, 62, 221, 163, 137, 114, 117, 166, 93, 236,
                131, 247, 45, 125, 155, 16, 62, 235, 54, 147, 54, 195, 212, 134, 107, 60, 143, 66, 218, 200, 29, 209,
                85, 238, 151, 75, 141, 29, 8, 70, 73, 158, 75, 174, 127, 3, 52, 99, 3, 40, 5, 159, 91, 208, 54, 123, 75,
                89, 158, 187, 156, 179, 158, 100, 116, 101, 240, 81, 87, 249, 206, 198, 1, 243, 95, 159, 22, 42, 162,
                131, 231, 45, 215, 68,
            ]),
        });
    });

    it('should reject auth if it is unable to verify server', async () => {
        const promise = getSrp(
            { Modulus: SERVER_MODULUS_FAKE, ServerEphemeral: '', Salt: '', Version: 4 },
            { password: '123' }
        );
        await assert.rejects(promise, {
            name: 'Error',
            message: 'Unable to verify server identity',
        });
    });
});
